import{_ as e,M as a,p as r,q as t,N as n,a1 as o}from"./framework-a7aa02af.js";const l={},c=o('<h1 id="kitx-加密环运作原理" tabindex="-1"><a class="header-anchor" href="#kitx-加密环运作原理" aria-hidden="true">#</a> KitX 加密环运作原理</h1><ol><li>每个客户端在初次启动时生成一对 RSA 密钥对存储在本地</li><li>用户需在设备页面选择新设备选择开始密钥交换过程来交换密钥</li><li>本机 (A) 会弹窗显示一个临时的八位数字密码, 密钥信息使用 AES 加密后发送给目标设备 (B)</li><li>目标设备 (B) 收到密钥信息后, 会弹窗提示用户输入临时密码, 输入正确后解密密钥信息并存储在本地</li><li>目标设备 (B) 成功解密后, 会将自己的密钥信息使用相同的临时密码加密后发送给本机 (A)</li><li>结束密钥交换过程</li><li>之后新的设备和加密环中任何一个设备配对, 都将自动开始与其他加密环中所有设备交换密钥</li></ol><h2 id="密钥交换过程" tabindex="-1"><a class="header-anchor" href="#密钥交换过程" aria-hidden="true">#</a> 密钥交换过程</h2>',3);function s(d,h){const i=a("Mermaid");return r(),t("div",null,[c,n(i,{id:"mermaid-43",code:""})])}const x=e(l,[["render",s],["__file","kitx-encryption-ring.html.vue"]]);export{x as default};
